mapper.select:
SELECT * FROM user
WHERE
delete_flag = false
AND password = #{password}

	AND phone_number = #{phoneNumber}


	AND email = #{email}

mapper.update:
UPDATE user SET version = NOW()

	,user_name = #{userName}


	,phone_number = #{phoneNumber}


	,email = #{email}


	,password = #{password}


	,image = #{image}


	,delete_flag = #{deleteFlag}

WHERE user_id = #{userId}
